CREATE DATABASE HelpDeskDB
GO

CREATE TABLE Customer (    Id INT IDENTITY(1,1) PRIMARY KEY,    FirstName NVARCHAR(30) NOT NULL,    LastName NVARCHAR (30) NOT NULL,    Email NVARCHAR (60) NOT NULL,	Phone NVARCHAR (15) NOT NULL,);CREATE TABLE Ticket (	Id INT IDENTITY (1,1) PRIMARY KEY,	CustomerId INT FOREIGN KEY REFERENCES Customer(Id),	Title NVARCHAR(40) NOT NULL,	Body NVARCHAR(MAX) NOT NULL,	TicketOpen BIT NOT NULL,	ResolvedBy INT FOREIGN KEY REFERENCES HelpAgent(Id));CREATE TABLE [Bookmarks](	Id INT IDENTITY(1,1) PRIMARY KEY	, TicketId INT FOREIGN KEY REFERENCES Ticket(Id)	, [Description] NVARCHAR(60) NOT NULL)CREATE TABLE [HelpAgent](	Id INT IDENTITY(1,1) PRIMARY KEY	, [Name] NVARCHAR(30) NOT NULL)INSERT INTO [Customer](	 FirstName	, LastName	, Email	, Phone)VALUES('Mazen', 'Saber' , 'mazen@example.com' , '313-500-0000'),('Andy' , 'Beneker' , 'andy@example.com' , '313-500-0001'),('John' , 'Smith' , 'john@example.com' , '313-500-0002')INSERT INTO [HelpAgent](	[Name])VALUES('Bill'),('Sally')INSERT INTO [Ticket](	CustomerId	, Title	, Body	, TicketOpen)Values(1 , 'Car wont start' , 'Help my car will not start!' , 1),(2 , 'Phone will not pair' , 'Can not pair my phone via bluetooth' , 1),(3 , 'AC is not cold' , 'AC blows warm air only' , 1)INSERT INTO [Bookmarks](	TicketId	, [Description])VALUES (2 , 'Favorite')ALTER TABLE [Bookmarks]ADD HelpAgentId INT FOREIGN KEY REFERENCES HelpAgent(Id)