CREATE DATABASE HelpDeskDB
GO

CREATE TABLE Customer (    Id INT IDENTITY(1,1) PRIMARY KEY,    FirstName NVARCHAR(30) NOT NULL,    LastName NVARCHAR (30) NOT NULL,    Email NVARCHAR (60) NOT NULL,	Phone NVARCHAR (15) NOT NULL,);CREATE TABLE Ticket (	Id INT IDENTITY (1,1) PRIMARY KEY,	CustomerId INT FOREIGN KEY REFERENCES Customer(Id),	ContactEmail NVARCHAR(65) NOT NULL,	Title NVARCHAR(40) NOT NULL,	Body NVARCHAR(MAX) NOT NULL,	TicketOpen BIT NOT NULL,	Bookmarked BIT,	ResolutionId INT FOREIGN KEY REFERENCES Resolution(Id));CREATE TABLE Resolution (	Id INT IDENTITY (1,1) PRIMARY KEY,	ResolutionType NVARCHAR (25) NOT NULL);